            });
            console.log("Message created");

        }
        catch(error){
          res.status(400).json({
            "status":"error",
            "message":error.message
          });
        }
    }

    static async getMessages(req,res) {
        try{
          const messages=await Contact.find();
          res.status(200).json({
            status:"success",
