            }
          })
        }
        catch(error){
         res.status(400).json({
            "status":"error",
            "message":error.message
         });

        }
    }

    static async deleteMessage(req,res){
        try{
            await Contact.findByIdAndDelete(req.params.id);
            res.status(204).json({
